INSERT INTO `comment` VALUES ('239', '21', '1', '304', '0', '# 2023.1.8第一次更新\r\n（a）用户登录：实现用户登录，并根据用户角色权限的不同，进行知识库的相应权限控制：管理员可以对知识发布进行审核，并有权进行删除的操作；用户可以对知识进行评论、收藏、分享和点赞的操作。\r\n（b）知识管理：发布者用户通过富文本编辑器将知识录入到数据库，管理员通过审核后可以实现知识分享，其他非发布者用户在知识发布后可以进行评论、收藏、分享和点赞。\r\n（c）知识首页：由知识搜索、知识展示排序、首页推荐这三大模块组成。\r\n知识搜索功能，需要通过建立数据库索引以及优化查询sql语句，或者使用ElasticSearch进行数据数据存储以及数据查询。\r\n知识展示排序：根据点赞数/收藏数/分享数，对查询得到的知识结果进行排序后展示\r\n首页推荐：参照各大论坛的首页推荐方法，对知识库中的内容根据既定算法进行相应的展示\r\n（d）个人中心：在个人中心可以查看我的发布、我的评论、我的收藏、我的点赞。同时新增草稿箱模块，将尚未编辑完成，还未发布的知识，自动存入草稿箱中\r\n', '0', '2023-01-08 14:55:36');
